sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

	var oGDTNow = new GlideDateTime();

	// query for overdue NeedIt Task Records with specific User in Field 'Assigned to'
	var grNeedItTask = new GlideRecord('x_58872_needit_needit_task');
	grNeedItTask.addQuery('due_date', '<=', oGDTNow.getValue());
	grNeedItTask.addQuery('state', '<', '3');
	grNeedItTask.addQuery('assigned_to', current.assigned_to);

	grNeedItTask.query();

	if (grNeedItTask.getRowCount() > 1) {
		// print Message to Notification
		template.print(gs.getMessage('cz.needit.task.notification.overduetasks.assignedto', [(grNeedItTask.getRowCount()-1)+'']));
	} 

})(current, template, email, email_action, event);
