sys_script_include.script:
var CZ_NeedIt_Email = Class.create();
CZ_NeedIt_Email.prototype = {
    initialize: function() {
    },

	validateEmailAddress: function(sEmail) {

		var bResult = false;

		// JavaScript String coercion
		sEmail = sEmail+'';

		// compare provided Email to RegEx-Pattern for Email validation
		if (sEmail.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)) {
			bResult = true;
		} else {
			bResult = false;
		}

		return bResult;
	},

    type: 'CZ_NeedIt_Email'
};
