sys_script_include.script:
var CZ_NeedIt_Utils = Class.create();
CZ_NeedIt_Utils.prototype = {
    initialize: function() {
    },


	/* Check if provided Date is in past
	* @param {string} sDate Date as String
	* @returns {boolean} true if Date is in Past, else false
	*/
	isDatePast: function(sDate) {

		var bResult = false;

		// create GlideDateTime Objects for current time and provided Date
		var oNow = new GlideDateTime();
		var oProvidedDate = new GlideDateTime(sDate+'');

		// check if provided Date is before current time
		if (oProvidedDate.before(oNow)) {
			bResult = true;
		} else {
			bResult = false;
		}

		return bResult;

	},

	/* Check if provided Date is today
	* @param {string} sDate Date as String
	* @returns {boolean} true if Date is on today, else false
	*/
	isDateToday: function(sDate) {

		var bResult = false;

		// create GlideDate Objects for current date and provided date
		var oToday = new GlideDateTime().getDate();
		var oProvidedDate = new GlideDateTime(sDate+'').getDate();

		// check if provided Date is same as today
		if (oToday == oProvidedDate) {
			bResult = true;
		} else {
			bResult = false;
		}

		return bResult;

	},

    type: 'CZ_NeedIt_Utils'
};
