sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // get the value of the user_name path parameter passed in the URL
	var sRequestUser = request.pathParams.user_name;
	// get the value of the ni_query query parameter passed in the URL
	var sEncodedQueryNI = request.queryParams.ni_query;

	// query the sys_user table to get the User Record for the passed user_name
	var grUser = new GlideRecord('sys_user');
	grUser.get('user_name', sRequestUser);


	// query for NeedIt Records with GlideAggregate
	var oNeedIt = new GlideAggregate('x_58872_needit_needit');
	oNeedIt.addAggregate('COUNT');
	oNeedIt.addQuery('u_requested_for', grUser.getValue('sys_id'));
	oNeedIt.addEncodedQuery(sEncodedQueryNI);
	oNeedIt.groupBy('u_request_type');
	oNeedIt.query();

	// initialize Result variables
	var iCountNeedItRecordsRequestType = 0;
	var aNeedItRecords = [];
	var iCountNeedItRecordsTotal = 0;

	// loop through queried NeedIt Record Aggregates
	while (oNeedIt.next()) {
		iCountNeedItRecordsRequestType = oNeedIt.getAggregate('COUNT');
		var sRequestType = oNeedIt.u_request_type.getDisplayValue();
		aNeedItRecords.push({"request_type": sRequestType, "count": iCountNeedItRecordsRequestType});
		iCountNeedItRecordsTotal = parseInt(iCountNeedItRecordsTotal) + parseInt(iCountNeedItRecordsRequestType);
	}

	// prepare Response by creating Body object
	var oResponseBody = {};
	oResponseBody.count_total_needit_records_user = iCountNeedItRecordsTotal;
	oResponseBody.needit_records = aNeedItRecords;
	oResponseBody.user = {"user_name": grUser.getValue('user_name'), "user_id": grUser.getValue('sys_id')};

	// send Response Object containing the Body object
	response.setBody(oResponseBody);
	response.setStatus(200);

})(request, response);
